import React, { useState, useEffect, useRef } from 'react';
import './App.css';

const App = () => {
  const [messages, setMessages] = useState([
    { sender: 'user', text: '넌 누구니?' },
    { sender: 'ai', text: "나는 vision-space의 RAG 선생님이다. 무엇이든 물어봐랏!" },
  ]);
  const [input, setInput] = useState('');
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSend = async () => {
    if (input.trim()) {
      setMessages([...messages, { sender: 'user', text: input }]);
      setInput('');
      // API 요청 처리 (생략)
    }
  };

  return (
    <div className="app-container">
      <div className="chat-container">
        <div className="messages" id="message-box">
          {messages.map((message, index) => (
            <div
              key={index}
              className={`message ${message.sender === 'user' ? 'user' : 'ai'}`}
            >
              <span className="sender">{message.sender === 'user' ? 'You' : 'RAG Assistant'}:</span>
              <span className="text"> {message.text}</span>
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>
        <div className="input-container">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="type to filter..."
            className="input-field"
          />
          <button onClick={handleSend} className="clear-button">
            send
          </button>
        </div>
      </div>
    </div>
  );
};

export default App;
